<?phpinclude '../Database/Connect.php';global $conn;if($_SERVER['REQUEST_METHOD'] === 'POST'){    $img = $_POST['img'];    $Title = $_POST['Title'];    $Message= $_POST['Message'];    // handling images upload    $FilePath = $_FILES['img']['tmp_name'];    $FileName = $_FILES['img']['name'];    $FileDir = './Announcement_images_uploaded/';    $Destination = $FileDir . $FileName;    move_uploaded_file($FilePath,$Destination);    $imageName = htmlspecialchars(basename($_FILES['img']['name']));    $Query = "INSERT INTO tb_announcement(image_name,Title,Message) VALUES ('$imageName','$Title','$Message')";    mysqli_query($conn,$Query);}