<?phpinclude '../Database/Connect.php';global $conn;if($_SERVER['REQUEST_METHOD'] === 'POST'){    // handling images upload    $FilePath = $_FILES['img']['tmp_name'];    $FileName = $_FILES['img']['name'];    $FileDir = './PHOTOS_upload/';    $Destination = $FileDir . $FileName;    move_uploaded_file($FilePath,$Destination);    $imageName = htmlspecialchars(basename($_FILES['img']['name']));    $Query = "INSERT INTO tb_photos (image) VALUES ('$imageName')";    mysqli_query($conn,$Query);}