<?phpinclude '../Database/Connect.php';global $conn;if ($_SERVER['REQUEST_METHOD'] === 'POST' && isset($_POST['id'])) {    $Title = htmlspecialchars($_POST['Title']);    $Message = htmlspecialchars($_POST['Message']);    $id = intval($_POST['id']); // Ensure $id is an integer    // Handling images upload    $FilePath = $_FILES['SaveImg']['tmp_name'];    $FileName = $_FILES['SaveImg']['name'];    $FileDir = './Event_uploaded_img/';    $Destination = $FileDir . $FileName;    if (move_uploaded_file($FilePath, $Destination)) {        $imageName = htmlspecialchars(basename($FileName));        // Use prepared statements for security        $stmt = $conn->prepare("UPDATE tb_event SET image = ?, title = ?, message = ? WHERE id = ?");        $stmt->bind_param("sssi", $imageName, $Title, $Message, $id);        if ($stmt->execute()) {            echo "Event updated successfully!";        } else {            echo "Error updating event: " . $stmt->error;        }        $stmt->close();    } else {        echo "Error uploading file.";    }}$conn->close();