<?phpinclude '../Database/Connect.php';global $conn;if ($_SERVER['REQUEST_METHOD'] === 'POST') {    $id = $_POST['id'];    // Check if new image is uploaded    if (isset($_FILES['SaveImg']) && $_FILES['SaveImg']['error'] == UPLOAD_ERR_OK) {        $FilePath = $_FILES['SaveImg']['tmp_name'];        $FileName = $_FILES['SaveImg']['name'];        $FileDir = './PHOTOS_upload/';        $Destination = $FileDir . $FileName;        if (move_uploaded_file($FilePath, $Destination)) {            $imageName = htmlspecialchars(basename($FileName));            // Update the photo with new image            $Query = "UPDATE tb_photos SET image = '$imageName' WHERE id = $id";            if (mysqli_query($conn, $Query)) {                echo "Photo updated successfully!";            } else {                echo "Error updating photo: " . mysqli_error($conn);            }        } else {            echo "Error uploading file.";            exit;        }    } else {        echo "No new image uploaded.";    }}$conn->close();