<?phpinclude '../Database/Connect.php';global $conn;if ($_SERVER['REQUEST_METHOD'] === 'POST' && isset($_POST['id'])) {    $Title = htmlspecialchars($_POST['Title']);    $Message = htmlspecialchars($_POST['Message']);    $id = intval($_POST['id']); // Ensure $id is an integer    // Handling image upload if exists    if (isset($_FILES['SaveImg']) && $_FILES['SaveImg']['error'] == UPLOAD_ERR_OK) {        $FilePath = $_FILES['SaveImg']['tmp_name'];        $FileName = $_FILES['SaveImg']['name'];        $FileDir = '../model/Add_Accomplishment_images/';        $Destination = $FileDir . $FileName;        if (move_uploaded_file($FilePath, $Destination)) {            $imageName = htmlspecialchars(basename($FileName));            // Update the prepared statement to include the image            $stmt = $conn->prepare("UPDATE accomplishment_tb SET image = ?, Title = ?, Message = ? WHERE id = ?");            $stmt->bind_param("sssi", $imageName, $Title, $Message, $id);        } else {            echo "Error uploading file.";            exit;        }    } else {        // If no new image is uploaded, update only title and message        $stmt = $conn->prepare("UPDATE accomplishment_tb SET Title = ?, Message = ? WHERE id = ?");        $stmt->bind_param("ssi", $Title, $Message, $id);    }    if ($stmt->execute()) {        echo "Accomplishment updated successfully!";    } else {        echo "Error updating accomplishment: " . $stmt->error;    }    $stmt->close();}$conn->close();?>