<?phpinclude '../Database/Connect.php';global $conn;if ($_SERVER['REQUEST_METHOD'] === 'POST' && isset($_POST['id'])) {    $Title = htmlspecialchars($_POST['Title']);    $Message = htmlspecialchars($_POST['Message']);    $id = intval($_POST['id']); // Ensure $id is an integer    // Prepare statement without image initially    $stmt = $conn->prepare("UPDATE tb_announcement SET Title = ?, Message = ? WHERE id = ?");    $stmt->bind_param("ssi", $Title, $Message, $id);    // Handling image upload if exists    if (isset($_FILES['SaveImg']) && $_FILES['SaveImg']['error'] == UPLOAD_ERR_OK) {        $FilePath = $_FILES['SaveImg']['tmp_name'];        $FileName = $_FILES['SaveImg']['name'];        $FileDir = './Announcement_images_uploaded/';        $Destination = $FileDir . $FileName;        if (move_uploaded_file($FilePath, $Destination)) {            $imageName = htmlspecialchars(basename($FileName));            // Update the prepared statement to include the image            $stmt = $conn->prepare("UPDATE tb_announcement SET image_name = ?, Title = ?, Message = ? WHERE id = ?");            $stmt->bind_param("sssi", $imageName, $Title, $Message, $id);        } else {            echo "Error uploading file.";            exit;        }    }    if ($stmt->execute()) {        echo "Event updated successfully!";    } else {        echo "Error updating event: " . $stmt->error;    }    $stmt->close();}$conn->close();?>